mapper.select:
SELECT * FROM ( SELECT id AS id, menu_name, NULL AS permission, pid AS pid, 0 AS is_checked FROM menu WHERE `status` = 1 UNION SELECT id, permission_name, permission, menu_id AS pid, CASE WHEN (id IN (
SELECT permission_id FROM permission mp LEFT JOIN role_permission mrp ON mp.id = mrp.permission_id WHERE mrp.role_id = #{roleId, jdbcType=VARCHAR} )) THEN 'true' ELSE 'false' END AS is_checked FROM permission
ORDER BY id ASC ) t;
