INSERT INTO article(id, creator, title, context, category_id, create_time, last_update_time, type, status, intro) VALUES ('359344220311388160', 'feihong', 'mysql 随机查询n条', '<pre><code>DROP TABLE IF EXISTS test_random;
CREATE TABLE `test_random` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `create_time` datetime DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

DROP FUNCTION IF EXISTS test_random_func;
CREATE FUNCTION test_random_func(num INT) RETURNS INT
BEGIN

    DECLARE n INT UNSIGNED DEFAULT 0;

    WHILE n &lt; num DO

    INSERT INTO test_random(`create_time`) VALUES(NOW());

    SET n=n+1;
    END WHILE;
    RETURN n;
END;

SELECT test_random_func(200000) AS ''数据条数'';

EXPLAIN SELECT id AS ''RAND()查询结果'' FROM test_random ORDER BY RAND() limit 0, 10;

SELECT
    t.id ''基于随机数查询结果''
FROM
    (SELECT
        ROUND(RAND() * (SELECT
                    MAX(id)
                FROM
                    test_random)) random_num,
            @num:=@num + 1
    FROM
        (SELECT @num:=0) AS a, test_random
    LIMIT 10) AS b,
    test_random AS t
WHERE
    b.random_num = t.id;</code></pre><p><br></p>', '359334630303727616', '2017-04-25 14:33:35', '2017-09-18 14:25:38', 'yiyi', 1, null);
INSERT INTO article(id, creator, title, context, category_id, create_time, last_update_time, type, status, intro) VALUES ('359344471940268032', 'feihong', 'mybatis-generator配置说明', '<p>mybatis generator config</p><p><!--StartFragment--><!--EndFragment--></p><pre><code><span><span><!--?</span-->xml version=<span>"1.0"</span> encoding=<span>"UTF-8"</span><span>?></span></span>

<span><<span>generatorConfiguration</span>></span>
    <span><<span>context</span> <span>id</span>=<span>"testTables"</span> <span>targetRuntime</span>=<span>"MyBatis3"</span>></span>
        <span><<span>commentGenerator</span>></span>
            <span><<span>property</span> <span>name</span>=<span>"suppressAllComments"</span> <span>value</span>=<span>"true"</span> /></span>
            <span><<span>property</span> <span>name</span>=<span>"suppressDate"</span> <span>value</span>=<span>"true"</span> /></span>
        <span><!--<span-->commentGenerator</span>></span></code><code>
        <span><<span>jdbcConnection</span> <span>driverClass</span>=<span>"com.mysql.jdbc.Driver"</span>
            <span>connectionURL</span>=<span>"jdbc:mysql://localhost:3306/ssi"</span> <span>userId</span>=<span>"root"</span>
            <span>password</span>=<span>"root"</span>></span>
        <span><!--<span-->jdbcConnection</span>>

        <span><<span>javaTypeResolver</span>></span>
            <span><<span>property</span> <span>name</span>=<span>"forceBigDecimals"</span> <span>value</span>=<span>"false"</span> /></span>
        <span><!--<span-->javaTypeResolver</span>>

        <span><<span>javaModelGenerator</span> <span>targetPackage</span>=<span>"com.mybatis.pojo"</span>
            <span>targetProject</span>=<span>"src/main/java"</span>></span>
            <span><!-- constructorBased:该属性只对MyBatis3有效，如果true就会使用构造方法入参，如果false就会使用setter方式。默认为false
                enableSubPackages:如果true，MBG会根据catalog和schema来生成子包。如果false就会直接用targetPackage属性。默认为false。
                immutable:该属性用来配置实体类属性是否可变，如果设置为true，那么constructorBased不管设置成什么，都会使用构造方法入参，并且不会生成setter方法。如果为false，实体类属性就可以改变。默认为false。
                rootClass:设置所有实体类的基类。如果设置，需要使用类的全限定名称。并且如果MBG能够加载rootClass，那么MBG不会覆盖和父类中完全匹配的属性。匹配规则：
                属性名完全相同 属性类型相同 属性有getter方法 属性有setter方法 trimStrings:是否对数据库查询结果进行trim操作，如果设置为true就会生成类似这样public
                void setUsername(String username) {this.username = username == null ? null
                : username.trim();}的setter方法。默认值为false --></span>
            <span><!-- enableSubPackages:是否让schema作为包的后缀 --></span>
            <span><<span>property</span> <span>name</span>=<span>"enableSubPackages"</span> <span>value</span>=<span>"false"</span> /></span>
            <span><!-- 从数据库返回的值被清理前后的空格 --></span>
            <span><<span>property</span> <span>name</span>=<span>"trimStrings"</span> <span>value</span>=<span>"true"</span> /></span>
        <span><!--<span-->javaModelGenerator</span>>

        <span><!-- targetProject:mapper映射文件生成的位置 --></span>
        <span><<span>sqlMapGenerator</span> <span>targetPackage</span>=<span>"com.mybatis.mapper"</span> <span>targetProject</span>=<span>"src/main/java"</span>></span>
            <span><!-- enableSubPackages:是否让schema作为包的后缀 --></span>
            <span><<span>property</span> <span>name</span>=<span>"enableSubPackages"</span> <span>value</span>=<span>"false"</span> /></span>
        <span><!--<span-->sqlMapGenerator</span>>

        <span><!-- targetPackage：mapper接口生成的位置 --></span>
        <span><<span>javaClientGenerator</span> <span>type</span>=<span>"XMLMAPPER"</span> <span>targetPackage</span>=<span>"com.mybatis.mapper"</span> <span>targetProject</span>=<span>"src/main/java"</span>></span>
            <span><!-- enableSubPackages:是否让schema作为包的后缀 --></span>
            <span><<span>property</span> <span>name</span>=<span>"enableSubPackages"</span> <span>value</span>=<span>"false"</span> /></span>
        <span><!--<span-->javaClientGenerator</span>>
        <span><!-- 指定数据库表 --></span>

        <span><<span>table</span> <span>domainObjectName</span>=<span>"User"</span>  <span>tableName</span>=<span>"tb_user"</span>></span><span><!--<span-->table</span>>
        <span><<span>table</span> <span>domainObjectName</span>=<span>"Order"</span>   <span>tableName</span>=<span>"tb_order"</span>></span><span><!--<span-->table</span>>
        <span><<span>table</span> <span>domainObjectName</span>=<span>"OrderdDetail"</span>   <span>tableName</span>=<span>"tb_orderdetail"</span>></span><span><!--<span-->table</span>>
        <span><<span>table</span> <span>domainObjectName</span>=<span>"Item"</span>   <span>tableName</span>=<span>"tb_item"</span>></span><span><!--<span-->table</span>>

        <span><<span>table</span> <span>schema</span>=<span>"&dbName;"</span> <span>tableName</span>=<span>"agent"</span> <span>domainObjectName</span>=<span>"Agent"</span> <span>enableCountByExample</span>=<span>"false"</span> <span>enableUpdateByExample</span>=<span>"false"</span> <span>enableDeleteByExample</span>=<span>"false"</span>
        <span>enableSelectByExample</span>=<span>"false"</span> <span>selectByExampleQueryId</span>=<span>"false"</span>></span>
          <span><<span>property</span> <span>name</span>=<span>"useActualColumnNames"</span> <span>value</span>=<span>"false"</span>/></span>
          <span><!--sqlStatement值也可以是SELECT UUID()或SELECT REPLACE(UUID(),''-'','''')--></span>
          <span><<span>generatedKey</span> <span>column</span>=<span>"id"</span> <span>sqlStatement</span>=<span>"MySql"</span> <span>identity</span>=<span>"true"</span> /></span>
          <span><!-- 重写字段类型，jdbcType为xxxMapper.xml中类型，javaType为java类型--></span>
          <span><<span>columnOverride</span> <span>column</span>=<span>"gender"</span> <span>jdbcType</span>=<span>"INTEGER"</span> <span>javaType</span>=<span>"Integer"</span> /></span>
          <span><<span>columnOverride</span> <span>column</span>=<span>"card_type"</span> <span>jdbcType</span>=<span>"INTEGER"</span> <span>javaType</span>=<span>"Integer"</span> /></span>
          <span><<span>columnOverride</span> <span>column</span>=<span>"check"</span> <span>jdbcType</span>=<span>"INTEGER"</span> <span>javaType</span>=<span>"Integer"</span> /></span>
          <span><<span>columnOverride</span> <span>column</span>=<span>"sys_flag"</span> <span>jdbcType</span>=<span>"INTEGER"</span> <span>javaType</span>=<span>"Integer"</span> /></span>
        <span><!--<span-->table</span>>
    <span><!--<span-->context</span>>

<span><!--<span-->generatorConfiguration</span>></code></pre>', '360444879895003136', '2017-09-21 15:19:25', '2017-09-21 15:19:25', 'yiyi', 1, null);
