INSERT INTO article(id, creator, title, context, category_id, create_time, last_update_time, type, status, intro) VALUES ('359736546775007232', 'feihong', 'centos下安装redis', '<p>1.到<a href="http://download.redis.io/releases/">http://download.redis.io/releases/</a>下找到需要的版本&nbsp;</p><p>2.</p><pre><code>wget http:<span>//download.redis.io/releases/redis-3.2.9.tar.gz</span>
