INSERT INTO article(id, creator, title, context, category_id, create_time, last_update_time, type, status, intro) VALUES ('421669249208025088', 'feihong', 'shell脚本批量操作git', '<pre><code><p>#批量git操作v2
#Moses
#使用方法：sh git.sh git checkout stable
#报错不终止，继续执行：sh git.sh -f git checkout stable

force=0
if [ "$1" == "-f" ]
	then
		force=1
		git_command=${@:2}
	else
		git_command=${@:1}
fi

current_pwd=`pwd`
success=1

find `pwd` -type d -name ".git" &gt; git.tmp
sed -i "s/\/.git/\//g" git.tmp

# 循环文件中的路径输入git命令
while read LINE
do
	echo $LINE
	cd "$LINE"

	eval $git_command

	if [ $? -eq 0 ]
	then
		echo "-----------------------------"
	else
		echo -e "\033[36;41m Error \033[0m"
		success=0

		if [ $force -eq 0 ]
		then
			break
		fi
	fi
done &lt; git.tmp

cd "$current_pwd"

rm -rf git.tmp

if [ $success -eq 1 ]
then
	echo -e "\033[37;42m Done!!! \033[0m"
fi

</p></code></pre>', '360444444962455552', '2018-03-09 14:03:03', '2018-07-09 09:51:24', 'yiyi', 1, null);
