INSERT INTO article(id, creator, title, context, category_id, create_time, last_update_time, type, status, intro) VALUES ('444246181736873984', 'feihong', '今天服务器莫名其妙不能访问了', '<p>症状：ping的通，网页打不开，nginx没有访问日志</p><p>关闭防火墙后恢复，说明防火墙端口没开</p><p>service iptables stop</p><p>一种方法是：</p><p>然后增加端口</p><p><!--StartFragment--><span style="color: rgb(79, 79, 79); font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; font-size: 16px; text-align: justify;">vim /etc/sysconfig/iptables</span><br></p><p>-A INPUT -p tcp -m state --state NEW -m tcp --dport 8080 -j ACCEPT</p><p><!--StartFragment-->service iptables start</p><p><br></p><p>另一种方法直接禁用开机启动</p><p>chkconfig iptables off</p><p>解决&nbsp;<br></p>', '444244886724542464', '2018-05-10 21:15:43', '2018-05-10 21:33:06', 'yiyi', 1, null);
INSERT INTO article(id, creator, title, context, category_id, create_time, last_update_time, type, status, intro) VALUES ('444542954627923968', 'feihong', 'centos中redis作为服务开机启动', '<p>创建服务文件：</p><p>vim&nbsp;<span style="letter-spacing: 0px;">/etc/systemd/system/redis-server.service</span></p><pre><code>[Unit]
Description=The redis-server Process Manager
After=network.target

[Service]
Type=notify
PIDFile=/var/run/redis.pid
ExecStart=<font color="#c24f4a">/usr/local/bin/redis-server /etc/redis.conf</font> --supervised systemd
